mapper.select:
SELECT visit_id, member_id, hospital_name, department, visit_date, cost, diagnosis
FROM visit
ORDER BY visit_date DESC;

SELECT *
FROM visit
WHERE DATE(visit_date) = #{date}
ORDER BY visit_date ASC;

SELECT COUNT(*) 
FROM visit 
WHERE DATE_FORMAT(visit_date, '%Y%m%d') = #{yyyyMMdd};

SELECT COUNT(*)
FROM visit v
JOIN family_member f ON v.member_id = f.member_id
WHERE DATE_FORMAT(v.visit_date, '%Y%m%d') = #{yyyyMMdd}
  AND f.user_id = #{userId};
