mapper.select:
SELECT v.visit_id, v.member_id, f.name AS member_name,
       v.hospital_name, v.department, v.visit_date, v.memo, v.cost
FROM visit v
JOIN family_member f ON v.member_id = f.member_id
WHERE f.user_id = #{user_id}
ORDER BY v.visit_date DESC;

SELECT visit_id, member_id, hospital_name, department, visit_date, memo, cost
FROM visit
WHERE visit_id = #{visit_id};
