mapper.select:
SELECT COUNT(*) 
FROM visit v
JOIN family_member f ON v.member_id = f.member_id
WHERE f.user_id = #{userId}
  AND DATE_FORMAT(v.visit_date, '%Y%m%d') = #{yyyyMMdd}

mapper.insert:
INSERT INTO visit (
    member_id, hospital_name, department, visit_date, cost, diagnosis
) VALUES (
    #{member_id}, #{hospital_name}, #{department}, #{visit_date}, #{cost}, #{diagnosis}
)
